
public class BinomischeFormel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a = 8;
		double b = 3;
		
		double Erstens = a * a;
		double Zweitens = 2 * a * b;
		double Drittens = b * b;
				
		double loesung1 = Erstens + Zweitens + Drittens;
		
		
		System.out.println(" (a+b)² = " + loesung1);
		
	}

}
